package lesson.day2;

public class Basic05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float splitNumber1 = 1234/1.0f;
		double splitNumber2 = 1234/1.0;
		float splitNumber3 = (float)(1234/1.0);
		
		System.out.println(splitNumber1);
		System.out.println(splitNumber2);
		System.out.println(splitNumber3);
		
		int x = 1;
		int y = 1;
		
		System.out.println("++x --> " + ++x);
		System.out.println("y++ --> " + y++);
		
		System.out.println("x --> " + x);
		System.out.println("y --> " + y);
	}

}
